package com.davidp.chessjourney.application.ui.settings;

import com.almasb.fxgl.dsl.FXGL;
import com.davidp.chessjourney.application.config.GlobalEventBus;
import com.davidp.chessjourney.application.domain.UserSavedAppEvent;
import com.davidp.chessjourney.application.ui.ScreenController;
import com.davidp.chessjourney.application.usecases.GetAllTagsUseCase;
import com.davidp.chessjourney.application.usecases.GetUserByIdUseCase;
import com.davidp.chessjourney.application.usecases.SaveUserUseCase;
import com.davidp.chessjourney.domain.User;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.scene.control.Button;
import javafx.scene.control.Label;
import javafx.scene.control.TextField;
import javafx.scene.image.ImageView;
import javafx.scene.layout.Pane;
import javafx.util.Duration;

import java.awt.*;
import java.util.concurrent.atomic.AtomicInteger;

public class SettingsViewController implements ScreenController {

  @FXML private Button btClose;


  @FXML
  private Button btProfile;


  @FXML private Button btSave;

  @FXML private ImageView imgClose;

  @FXML private TextField inEmail;

  @FXML private TextField inLastName;

  @FXML private TextField inName;

  @FXML private Label lbUser;

  @FXML private Pane pnlTitleBar1111;

  @FXML private Pane pnlTitleBar11111;

  @FXML private Pane pnlTitleBar11112;

  @FXML private Pane rootPane;


  @FXML
  private Pane pnlProfile;


  @FXML
  private Pane pnlTags;


  @FXML
  private TextField inTag;

  @FXML
  private Button btOptionTags;


  @FXML
  private Button btAddTag;


  private GetAllTagsUseCase getAllTagsUseCase;
  private GetUserByIdUseCase getUserByIdUseCase;
  private SaveUserUseCase saveUserUseCase;
  private SettingsViewInputScreenData settingsViewData;

  private ScreenController.ScreenStatus status;

  public void setGetUserByIdUseCase(GetUserByIdUseCase getUserByIdUseCase) {

    this.getUserByIdUseCase = getUserByIdUseCase;
  }

  public void setSaveUserUseCase(SaveUserUseCase saveUserUseCase) {

    this.saveUserUseCase = saveUserUseCase;
  }

  public void setGetAllTagsUseCase(GetAllTagsUseCase getAllTagsUseCase) {

    this.getAllTagsUseCase = getAllTagsUseCase;
  }

  public void setSettingsViewData(SettingsViewInputScreenData settingsViewData) {

    this.settingsViewData = settingsViewData;
  }

  public void refreshUserInfo(final long userId) {

    User user = getUserByIdUseCase.execute(userId);

    inName.setText(user.getFirstname());
    inLastName.setText(user.getLastname());
    inEmail.setText(user.getEmail());
    lbUser.setText(user.getInitials());
  }

  public void refreshTags() {

    // pnlTags.getChildren().filtered(p-> "tag".equalsIgnoreCase(p.getId())).clear();

    AtomicInteger pos = new AtomicInteger(10);
    getAllTagsUseCase
            .execute()
            .forEach(
                    tag -> {
                      Label label = new Label(tag.getName());
                      label.setId("tag");
                      label.getStyleClass().add("text-white-medium");
                      label.setText(tag.getName());
                      label.setLayoutX(10);
                      label.setLayoutY(pos.get());
                      pos.set(pos.get() + 25);
                      pnlTags.getChildren().add(label);
                    });
  }

  public void initialize() {

    status = ScreenController.ScreenStatus.INITIALIZED;
  }

  @FXML
  void buttonAction(ActionEvent event) {

    if (event.getSource() == btSave) {

      User user = buildUserFromForm();
      saveUserUseCase.execute(user);
      GlobalEventBus.get().post(new UserSavedAppEvent(settingsViewData.getUserId()));
      rootPane.setVisible(false);
    }

    if (event.getSource() == btClose) {

      rootPane.setVisible(false);
    }

    if (event.getSource() == btOptionTags) {

      pnlProfile.setVisible(false);
      FXGL.animationBuilder()
              .duration(Duration.seconds(0.2))
              .onFinished(
                      () -> {
                        refreshTags();
                        pnlTags.setVisible(true);
                        //TODO set status = ....
                      })
              .fadeIn(pnlTags)
              .buildAndPlay();
    }

    if (event.getSource() == btProfile) {
      pnlTags.setVisible(false);
      pnlProfile.setVisible(true);
    }


  }

  private User buildUserFromForm() {

    // TODO validate user data..
    // long id, String email, String firstname, String lastname
    User user =
        new User(
            settingsViewData.getUserId(),
            inEmail.getText(),
            inName.getText(),
            inLastName.getText());
    return user;
  }

  public void setData(InputScreenData inputData) {

    SettingsViewInputScreenData settingsViewData = (SettingsViewInputScreenData) inputData;
    setSettingsViewData(settingsViewData);
    refreshUserInfo(settingsViewData.getUserId());

    if (inputData.isLayoutInfoValid()) {

      setLayout(inputData.getLayoutX(), inputData.getLayoutY());
    }


  }

  @Override
  public void setLayout(double layoutX, double layoutY) {

    rootPane.setLayoutX(layoutX);
    rootPane.setLayoutY(layoutY);
  }

  @Override
  public void show() {

    rootPane.setVisible(true);
    rootPane.toFront();
  }

  public void show(InputScreenData inputData) {

    setData(inputData);
    status = ScreenController.ScreenStatus.VISIBLE;
    show();
  }

  @Override
  public void hide() {
    status = ScreenController.ScreenStatus.HIDDEN;
    rootPane.setVisible(false);
  }

  @Override
  public boolean isVisible() {
    return rootPane.isVisible();
  }

  @Override
  public boolean isHidden() {
    return !rootPane.isVisible();
  }

  @Override
  public Pane getRootPane() {
    return rootPane;
  }

  @Override
  public ScreenStatus getStatus() {
    return null;
  }

  @Override
  public boolean isInitialized() {

    return status == ScreenController.ScreenStatus.INITIALIZED;
  }
}

package com.davidp.chessjourney.application.ui.settings;

import com.almasb.fxgl.dsl.FXGL;
import com.davidp.chessjourney.application.config.GlobalEventBus;
import com.davidp.chessjourney.application.domain.UserSavedAppEvent;
import com.davidp.chessjourney.application.ui.ScreenController;
import com.davidp.chessjourney.application.usecases.GetAllTagsUseCase;
import com.davidp.chessjourney.application.usecases.GetUserByIdUseCase;
import com.davidp.chessjourney.application.usecases.SaveUserUseCase;
import com.davidp.chessjourney.domain.User;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.scene.control.Button;
import javafx.scene.control.Label;
import javafx.scene.control.TextField;
import javafx.scene.image.ImageView;
import javafx.scene.layout.Pane;
import javafx.util.Duration;

import java.awt.*;
import java.util.concurrent.atomic.AtomicInteger;

public class SettingsViewController implements ScreenController {

  @FXML private Button btClose;


  @FXML
  private Button btProfile;


  @FXML private Button btSave;

  @FXML private ImageView imgClose;

  @FXML private TextField inEmail;

  @FXML private TextField inLastName;

  @FXML private TextField inName;

  @FXML private Label lbUser;

  @FXML private Pane pnlTitleBar1111;

  @FXML private Pane pnlTitleBar11111;

  @FXML private Pane pnlTitleBar11112;

  @FXML private Pane rootPane;


  @FXML
  private Pane pnlProfile;


  @FXML
  private Pane pnlTags;


  @FXML
  private TextField inTag;

  @FXML
  private Button btOptionTags;


  @FXML
  private Button btAddTag;


  private GetAllTagsUseCase getAllTagsUseCase;
  private GetUserByIdUseCase getUserByIdUseCase;
  private SaveUserUseCase saveUserUseCase;
  private SettingsViewInputScreenData settingsViewData;

  private ScreenController.ScreenStatus status;

  public void setGetUserByIdUseCase(GetUserByIdUseCase getUserByIdUseCase) {

    this.getUserByIdUseCase = getUserByIdUseCase;
  }

  public void setSaveUserUseCase(SaveUserUseCase saveUserUseCase) {

    this.saveUserUseCase = saveUserUseCase;
  }

  public void setGetAllTagsUseCase(GetAllTagsUseCase getAllTagsUseCase) {

    this.getAllTagsUseCase = getAllTagsUseCase;
  }

  public void setSettingsViewData(SettingsViewInputScreenData settingsViewData) {

    this.settingsViewData = settingsViewData;
  }

  public void refreshUserInfo(final long userId) {

    User user = getUserByIdUseCase.execute(userId);

    inName.setText(user.getFirstname());
    inLastName.setText(user.getLastname());
    inEmail.setText(user.getEmail());
    lbUser.setText(user.getInitials());
  }

  public void refreshTags() {

    // pnlTags.getChildren().filtered(p-> "tag".equalsIgnoreCase(p.getId())).clear();

    AtomicInteger pos = new AtomicInteger(10);
    getAllTagsUseCase
            .execute()
            .forEach(
                    tag -> {
                      Label label = new Label(tag.getName());
                      label.setId("tag");
                      label.getStyleClass().add("text-white-medium");
                      label.setText(tag.getName());
                      label.setLayoutX(10);
                      label.setLayoutY(pos.get());
                      pos.set(pos.get() + 25);
                      pnlTags.getChildren().add(label);
                    });
  }

  public void initialize() {

    status = ScreenController.ScreenStatus.INITIALIZED;
  }

  @FXML
  void buttonAction(ActionEvent event) {

    if (event.getSource() == btSave) {

      User user = buildUserFromForm();
      saveUserUseCase.execute(user);
      GlobalEventBus.get().post(new UserSavedAppEvent(settingsViewData.getUserId()));
      rootPane.setVisible(false);
    }

    if (event.getSource() == btClose) {

      rootPane.setVisible(false);
    }

    if (event.getSource() == btOptionTags) {

      pnlProfile.setVisible(false);
      JavaFXAnimationUtil.animationBuilder()
              .duration(Duration.seconds(0.2))
              .onFinished(
                      () -> {
                        refreshTags();
                        pnlTags.setVisible(true);
                        //TODO set status = ....
                      })
              .fadeIn(pnlTags)
              .buildAndPlay();
    }

    if (event.getSource() == btProfile) {
      pnlTags.setVisible(false);
      pnlProfile.setVisible(true);
    }


  }

  private User buildUserFromForm() {

    // TODO validate user data..
    // long id, String email, String firstname, String lastname
    User user =
        new User(
            settingsViewData.getUserId(),
            inEmail.getText(),
            inName.getText(),
            inLastName.getText());
    return user;
  }

  public void setData(InputScreenData inputData) {

    SettingsViewInputScreenData settingsViewData = (SettingsViewInputScreenData) inputData;
    setSettingsViewData(settingsViewData);
    refreshUserInfo(settingsViewData.getUserId());

    if (inputData.isLayoutInfoValid()) {

      setLayout(inputData.getLayoutX(), inputData.getLayoutY());
    }


  }

  @Override
  public void setLayout(double layoutX, double layoutY) {

    rootPane.setLayoutX(layoutX);
    rootPane.setLayoutY(layoutY);
  }

  @Override
  public void show() {

    rootPane.setVisible(true);
    rootPane.toFront();
  }

  public void show(InputScreenData inputData) {

    setData(inputData);
    status = ScreenController.ScreenStatus.VISIBLE;
    show();
  }

  @Override
  public void hide() {
    status = ScreenController.ScreenStatus.HIDDEN;
    rootPane.setVisible(false);
  }

  @Override
  public boolean isVisible() {
    return rootPane.isVisible();
  }

  @Override
  public boolean isHidden() {
    return !rootPane.isVisible();
  }

  @Override
  public Pane getRootPane() {
    return rootPane;
  }

  @Override
  public ScreenStatus getStatus() {
    return null;
  }

  @Override
  public boolean isInitialized() {

    return status == ScreenController.ScreenStatus.INITIALIZED;
  }
}

package com.davidp.chessjourney.application.util;

import javafx.animation.FadeTransition;
import javafx.scene.Node;
import javafx.util.Duration;

public class JavaFXAnimationUtil {
    public static AnimationBuilder animationBuilder() {
        return new AnimationBuilder();
    }

    public static class AnimationBuilder {
        private Duration duration = Duration.seconds(0.2);
        private Runnable onFinished;
        private Node node;
        private boolean fadeIn = true;

        public AnimationBuilder duration(Duration duration) {
            this.duration = duration;
            return this;
        }

        public AnimationBuilder onFinished(Runnable onFinished) {
            this.onFinished = onFinished;
            return this;
        }

        public AnimationBuilder fadeIn(Node node) {
            this.node = node;
            this.fadeIn = true;
            return this;
        }

        public AnimationBuilder fadeOut(Node node) {
            this.node = node;
            this.fadeIn = false;
            return this;
        }

        public void buildAndPlay() {
            if (node == null) return;
            FadeTransition ft = new FadeTransition(duration, node);
            if (fadeIn) {
                ft.setFromValue(0.0);
                ft.setToValue(1.0);
            } else {
                ft.setFromValue(1.0);
                ft.setToValue(0.0);
            }
            if (onFinished != null) {
                ft.setOnFinished(e -> onFinished.run());
            }
            ft.play();
        }
    }
}

package com.davidp.chessjourney.application.util;

import javafx.animation.FadeTransition;
import javafx.animation.KeyFrame;
import javafx.animation.SequentialTransition;
import javafx.animation.Timeline;
import javafx.animation.TranslateTransition;
import javafx.scene.Node;
import javafx.scene.layout.Pane;
import javafx.scene.paint.Color;
import javafx.scene.shape.Rectangle;
import javafx.util.Duration;

import java.util.Random;

public class JavaFXAnimationUtil {
    public static AnimationBuilder animationBuilder() {
        return new AnimationBuilder();
    }

    public static class AnimationBuilder {
        private Duration duration = Duration.seconds(0.2);
        private Runnable onFinished;
        private Node node;
        private boolean fadeIn = true;

        public AnimationBuilder duration(Duration duration) {
            this.duration = duration;
            return this;
        }

        public AnimationBuilder onFinished(Runnable onFinished) {
            this.onFinished = onFinished;
            return this;
        }

        public AnimationBuilder fadeIn(Node node) {
            this.node = node;
            this.fadeIn = true;
            return this;
        }

        public AnimationBuilder fadeOut(Node node) {
            this.node = node;
            this.fadeIn = false;
            return this;
        }

        public void buildAndPlay() {
            if (node == null) return;
            FadeTransition ft = new FadeTransition(duration, node);
            if (fadeIn) {
                ft.setFromValue(0.0);
                ft.setToValue(1.0);
            } else {
                ft.setFromValue(1.0);
                ft.setToValue(0.0);
            }
            if (onFinished != null) {
                ft.setOnFinished(e -> onFinished.run());
            }
            ft.play();
        }
    }

    /**
     * Efecto de niebla animada con píxeles grises ascendentes.
     *
     * @param targetPane      Pane donde se dibuja la niebla
     * @param durationSegundos Duración total de la animación en segundos
     */
    public static void playFogEffect(Pane targetPane, double durationSegundos) {
        Random random = new Random();
        int width = (int) targetPane.getWidth();
        int height = (int) targetPane.getHeight();
        int numClouds = 6 + random.nextInt(4); // 6-9 agrupaciones
        SequentialTransition allClouds = new SequentialTransition();
        for (int c = 0; c < numClouds; c++) {
            int cloudWidth = 80 + random.nextInt(60); // 80-140 px
            int cloudHeight = 30 + random.nextInt(20); // 30-50 px
            int baseX = random.nextInt(Math.max(1, width - cloudWidth));
            int baseY = height - 30 - random.nextInt(30); // parte baja
            int density = 18 + random.nextInt(10); // píxeles por nube
            for (int i = 0; i < density; i++) {
                int px = baseX + random.nextInt(cloudWidth);
                int py = baseY + random.nextInt(cloudHeight);
                int size = 3 + random.nextInt(4); // 3-6 px
                Rectangle pixel = new Rectangle(size, size, Color.rgb(180 + random.nextInt(40), 180 + random.nextInt(40), 180 + random.nextInt(40), 0.45 + 0.25 * random.nextDouble()));
                pixel.setArcWidth(size * 0.7);
                pixel.setArcHeight(size * 0.7);
                pixel.setX(px);
                pixel.setY(py);
                targetPane.getChildren().add(pixel);
                double moveY = -60 - random.nextInt(40); // suben 60-100px
                double delay = random.nextDouble() * (durationSegundos * 0.5); // dispersión
                TranslateTransition tt = new TranslateTransition(Duration.seconds(durationSegundos * (0.7 + 0.5 * random.nextDouble())), pixel);
                tt.setFromY(0);
                tt.setToY(moveY);
                tt.setDelay(Duration.seconds(delay));
                tt.setOnFinished(e -> targetPane.getChildren().remove(pixel));
                allClouds.getChildren().add(tt);
            }
        }
        allClouds.setOnFinished(e -> {}); // nada extra
        allClouds.play();
    }
}

package com.davidp.chessjourney.application.util;

import javafx.animation.Animation;
import javafx.animation.KeyFrame;
import javafx.util.Duration;

    public static void runLoop(Runnable action, Duration interval) {
        Timeline timeline = new Timeline(new KeyFrame(interval, e -> action.run()));
        timeline.setCycleCount(Timeline.INDEFINITE);
        timeline.play();
        addTimer(timeline);
    }
}
import java.util.List;

public class JavaFXGameTimerUtil {
    private static final List<Animation> activeTimers = new ArrayList<>();

    public static void addTimer(Animation timer) {
        activeTimers.add(timer);
    }

    public static void clear() {
        for (Animation timer : activeTimers) {
            timer.stop();
        }
        activeTimers.clear();
    }
}

package com.davidp.chessjourney.application.util;

import javafx.animation.Animation;
import javafx.animation.KeyFrame;
import javafx.animation.Timeline;
import javafx.util.Duration;
import java.util.ArrayList;
import java.util.List;

public class JavaFXGameTimerUtil {
    private static final List<Animation> activeTimers = new ArrayList<>();

    public static void runLoop(Runnable action, Duration interval) {
        Timeline timeline = new Timeline(new KeyFrame(interval, e -> action.run()));
        timeline.setCycleCount(Timeline.INDEFINITE);
        timeline.play();
        addTimer(timeline);
    }

    public static void addTimer(Animation timer) {
        activeTimers.add(timer);
    }

    public static void clear() {
        for (Animation timer : activeTimers) {
            timer.stop();
        }
        activeTimers.clear();
    }
}
