package com.davidp.chessjourney.api;

import com.davidp.chessjourney.application.config.AppProperties;
import com.davidp.chessjourney.application.usecases.GetUserByIdUseCase;
import com.davidp.chessjourney.domain.User;
import io.javalin.Javalin;
import io.javalin.http.Context;

public class ActiveUserApiController {
    private final GetUserByIdUseCase getUserByIdUseCase;

    public ActiveUserApiController(GetUserByIdUseCase getUserByIdUseCase) {
        this.getUserByIdUseCase = getUserByIdUseCase;
    }

    public void registerRoutes(Javalin app) {
        app.get("/chessjourney/activeUser", this::getActiveUser);
    }

    private void getActiveUser(Context ctx) {
        long activeUserId = AppProperties.getInstance().getActiveUserId();
        User user = getUserByIdUseCase.execute(activeUserId);
        if (user == null) {
            ctx.status(404).result("No active user found");
            return;
        }
        ctx.json(new UserResponse(user));
    }

    public static class UserResponse {
        public long id;
        public String firstname;
        public String lastname;
        public String email;
        public String initials;

        public UserResponse(User user) {
            this.id = user.getId();
            this.firstname = user.getFirstname();
            this.lastname = user.getLastname();
            this.email = user.getEmail();
            this.initials = user.getInitials();
        }
    }
}

package com.davidp.chessjourney.api;

import com.davidp.chessjourney.application.config.AppProperties;
import com.davidp.chessjourney.application.usecases.GetUserByIdUseCase;
import com.davidp.chessjourney.domain.User;
import io.javalin.Javalin;
import io.javalin.http.Context;

public class ActiveUserController {
    private final GetUserByIdUseCase getUserByIdUseCase;
    private final Javalin app;

    public ActiveUserController(GetUserByIdUseCase getUserByIdUseCase) {
        this.getUserByIdUseCase = getUserByIdUseCase;
        this.app = Javalin.create();
        configureRoutes();
    }

    private void configureRoutes() {
        app.get("/chessjourney/activeUser", this::getActiveUser);
    }

    private void getActiveUser(Context ctx) {
        long activeUserId = AppProperties.getInstance().getActiveUserId();
        User user = getUserByIdUseCase.execute(activeUserId);

        if (user == null) {
            ctx.status(404).json(new ErrorResponse("No active user found"));
            return;
        }

        ctx.json(new UserResponse(user));
    }

    public void start(int port) {
        app.start(port);
    }

    public void stop() {
        app.stop();
    }

    private static class UserResponse {
        private final long id;
        private final String firstname;
        private final String lastname;
        private final String email;
        private final String initials;

        public UserResponse(User user) {
            this.id = user.getId();
            this.firstname = user.getFirstname();
            this.lastname = user.getLastname();
            this.email = user.getEmail();
            this.initials = user.getInitials();
        }

        // Getters necesarios para la serializaciÃ³n JSON
        public long getId() { return id; }
        public String getFirstname() { return firstname; }
        public String getLastname() { return lastname; }
        public String getEmail() { return email; }
        public String getInitials() { return initials; }
    }

    private static class ErrorResponse {
        private final String message;

        public ErrorResponse(String message) {
            this.message = message;
        }

        public String getMessage() { return message; }
    }
}

package com.davidp.chessjourney.application.factories;

import javafx.scene.media.AudioClip;
import java.net.URL;
import java.util.EnumMap;
import java.util.Optional;

public class JavaFXSoundUtil {
    private final EnumMap<Enum<?>, AudioClip> soundCache = new EnumMap<>(Enum.class);
    private final ClassLoader classLoader = getClass().getClassLoader();

    public <E extends Enum<E>> void preloadSounds(Class<E> enumClass, SoundResourceProvider<E> provider) {
        for (E type : enumClass.getEnumConstants()) {
            String resource = provider.getResourceName(type);
            URL url = classLoader.getResource("assets/sounds/" + resource);
            if (url != null) {
                soundCache.put(type, new AudioClip(url.toExternalForm()));
            }
        }
    }

    public void playSound(Enum<?> type) {
        Optional.ofNullable(soundCache.get(type)).ifPresent(AudioClip::play);
    }

    public interface SoundResourceProvider<E> {
        String getResourceName(E type);
    }
}

package com.davidp.chessjourney.application.factories;

import javafx.scene.media.AudioClip;
import java.net.URL;
import java.util.EnumMap;
import java.util.Optional;

public class JavaFXSoundUtil<E extends Enum<E>> {
    private final EnumMap<E, AudioClip> soundCache;
    private final ClassLoader classLoader = getClass().getClassLoader();

    public JavaFXSoundUtil(Class<E> enumClass, SoundResourceProvider<E> provider) {
        soundCache = new EnumMap<>(enumClass);
        for (E type : enumClass.getEnumConstants()) {
            String resource = provider.getResourceName(type);
            URL url = classLoader.getResource("assets/sounds/" + resource);
            if (url != null) {
                soundCache.put(type, new AudioClip(url.toExternalForm()));
            }
        }
    }

    public void playSound(E type) {
        Optional.ofNullable(soundCache.get(type)).ifPresent(AudioClip::play);
    }

    public interface SoundResourceProvider<T> {
        String getResourceName(T type);
    }
}

package com.davidp.chessjourney.application.factories;

import java.util.EnumMap;
import java.util.Optional;

/** This class is responsible for managing and playing game sounds. */
public class SoundServiceFactory {

  /** Enum for managing the sounds of the game. */
  public enum SoundType {
    PIECE_PLACEMENT("piece-placement.wav"),
    PIECE_PLACEMENT_ERROR("piece-placement-error.wav"),
    FAIL_EXERCISE("move-fail.wav"),
    SUCCEED_EXERCISE("move-success.wav"),
    NEW_GAME("new-game.wav");

    private final String resourcePath;

    SoundType(final String resourcePath) {
      this.resourcePath = resourcePath;
    }

    public String resourceName() {
      return this.resourcePath;
    }
  }

  private static volatile SoundServiceFactory instance;
  private final JavaFXSoundUtil soundUtil;

  private SoundServiceFactory() {
    soundUtil = new JavaFXSoundUtil();
    soundUtil.preloadSounds(SoundType.class, SoundType::resourceName);
  }

  public static SoundServiceFactory getInstance() {
    if (instance == null) {
      synchronized (SoundServiceFactory.class) {
        if (instance == null) {
          instance = new SoundServiceFactory();
        }
      }
    }
    return instance;
  }

  /**
   * Plays the specified sound type.
   *
   * @param soundType the type of sound to play
   */
  public void playSound(SoundType soundType) {
    soundUtil.playSound(soundType);
  }
}

