package com.davidp.chessjourney.api;

import com.davidp.chessjourney.application.config.AppProperties;
import com.davidp.chessjourney.application.usecases.GetUserByIdUseCase;
import com.davidp.chessjourney.domain.User;
import io.javalin.Javalin;
import io.javalin.http.Context;

public class ActiveUserApiController {
    private final GetUserByIdUseCase getUserByIdUseCase;

    public ActiveUserApiController(GetUserByIdUseCase getUserByIdUseCase) {
        this.getUserByIdUseCase = getUserByIdUseCase;
    }

    public void registerRoutes(Javalin app) {
        app.get("/chessjourney/activeUser", this::getActiveUser);
    }

    private void getActiveUser(Context ctx) {
        long activeUserId = AppProperties.getInstance().getActiveUserId();
        User user = getUserByIdUseCase.execute(activeUserId);
        if (user == null) {
            ctx.status(404).result("No active user found");
            return;
        }
        ctx.json(new UserResponse(user));
    }

    public static class UserResponse {
        public long id;
        public String firstname;
        public String lastname;
        public String email;
        public String initials;

        public UserResponse(User user) {
            this.id = user.getId();
            this.firstname = user.getFirstname();
            this.lastname = user.getLastname();
            this.email = user.getEmail();
            this.initials = user.getInitials();
        }
    }
}

package com.davidp.chessjourney.api;

import io.javalin.Javalin;
import com.davidp.chessjourney.application.factories.UseCaseFactory;

public class ApiConfig {

    private static volatile ApiConfig instance = null;

    private final Javalin app;

    private ApiConfig() {

        this.app = Javalin.create();
        configureRoutes();
    }

    public static ApiConfig getInstance() {

        if (instance == null) {
            synchronized (ApiConfig.class) {
                if (instance == null) {

                    instance = new ApiConfig();
                }
            }
        }
        return instance;
    }

    private void configureRoutes() {

        QuoteController quoteController = new QuoteController(UseCaseFactory.createSaveQuoteUseCase());

        ActiveUserController activeUserController = new ActiveUserController(UseCaseFactory.createGetUserByIdUseCase());

        app.post("/chessjourney/quote", quoteController.saveQuote);
        app.get("/chessjourney/activeUser", activeUserController.getActiveUser);

    }

    public void start(int port) {

        app.start(port);
    }

    public void stop() {

        app.stop();
    }
}

package com.davidp.chessjourney.api;

import io.javalin.Javalin;
import com.davidp.chessjourney.application.factories.UseCaseFactory;
import com.davidp.chessjourney.application.usecases.GetUserByIdUseCase;
import com.davidp.chessjourney.application.usecases.SaveQuoteUseCase;

public class ApiConfig {

    private static volatile ApiConfig instance = null;

    private final Javalin app;
    private final QuoteController quoteController;
    private final ActiveUserController activeUserController;

    private ApiConfig(SaveQuoteUseCase saveQuoteUseCase, GetUserByIdUseCase getUserByIdUseCase) {
        this.app = Javalin.create();
        this.quoteController = new QuoteController(saveQuoteUseCase);
        this.activeUserController = new ActiveUserController(getUserByIdUseCase);
        configureRoutes();
    }

    public static ApiConfig getInstance() {
        if (instance == null) {
            synchronized (ApiConfig.class) {
                if (instance == null) {
                    SaveQuoteUseCase saveQuoteUseCase = UseCaseFactory.createSaveQuoteUseCase();
                    GetUserByIdUseCase getUserByIdUseCase = UseCaseFactory.createGetUserByIdUseCase();
                    instance = new ApiConfig(saveQuoteUseCase, getUserByIdUseCase);
                }
            }
        }
        return instance;
    }

    private void configureRoutes() {
        app.post("/chessjourney/quote", quoteController.saveQuote);
        app.get("/chessjourney/activeUser", activeUserController.getActiveUser);
    }

    public void start(int port) {
        app.start(port);
    }

    public void stop() {
        app.stop();
    }
}

package com.davidp.chessjourney.api;

import com.davidp.chessjourney.application.usecases.SaveQuoteUseCase;
import com.davidp.chessjourney.domain.Quote;
import io.javalin.http.Handler;

public class QuoteController {

    private SaveQuoteUseCase saveQuoteUseCase = null;

    public QuoteController(SaveQuoteUseCase saveQuoteUseCase) {

        this.saveQuoteUseCase = saveQuoteUseCase;
    }

    public Handler saveQuote = ctx -> {

        QuoteRequest request = ctx.bodyAsClass(QuoteRequest.class);
        Quote savedQuote = saveQuoteUseCase.execute(request.text, request.author);
        ctx.json(savedQuote);
    };

    private static class QuoteRequest {

        public String text;
        public String author;
    }
}

